import React from 'react';
import Sidebar from './Sidebar'
import Chat from './Chat'

class App extends React.Component {
  render() {

    this.props.initialData.channels.map(channelObj => channelObj.name)

    console.log('Im the App components props ', this.props)

    return (
      <div>
        <h1>{this.props.greeting} welcome to otter chat!</h1>
          <Sidebar channels={this.props.initialData.channels} user={this.props.initialData.current_user} />
        <Chat />
      </div>
    );
  }
}

export default App;
