import React from "react";

class ChannelList extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      channels: props.channels,
      newChannelName: ''
    }
  }

  handleChange = (e) => {
    this.setState({ newChannelName: e.target.value })
  }

  handleClick = () => {
    this.setState({ channels: [...this.state.channels, `#${this.state.newChannelName}`] })
  }

  handleChannelClick = () => {
    this.props.onChangeChannel('hello!!')
  }

  render() {
    const channelLis = this.state.channels.map(channelName => <li onClick={this.handleChannelClick} key={channelName}>{channelName}</li>)
    return (
      <div>
        <h4 style={{marginBottom: 5}}>Channels</h4>
        <input value={this.state.newChannelName} onChange={this.handleChange} type="text" placeholder="add a channel" />
        <button onClick={this.handleClick}>+</button>
        <ul>
          {channelLis}
        </ul>
      </div>
    );
  }
}

export default ChannelList;
