    const messages = this.props.channel.messages.map(messageObj => <Message />)

    return (
      <div>
        <h2>{this.props.channel.name}</h2>
        <Message />
        <Message />
        <Message />
        <MessageForm />
      </div>
    )
  }
}

export default Chat
